from transformers import AutoTokenizer

# 选择一个模型的分词器
tokenizer = AutoTokenizer.from_pretrained("/home/models/Llama-3.2-3B")  # 换成你使用的模型，比如 'bert-base-uncased'

text = "###Question: What are the ids of courses without prerequisites?,###Database_ID:college_2,###Full_database_schema: CREATE TABLE `classroom` ( building varchar(15) PRIMARY KEY, room_number varchar(7) PRIMARY KEY, capacity numeric(4,0) ); Sample rows from `classroom`: (Lamberton, 134, 10), (Chandler, 375, 10), (Fairchild, 145, 27), CREATE TABLE `department` ( dept_name varchar(20) PRIMARY KEY, building varchar(15), budget numeric(12,2) ); Sample rows from `department`: (Civil Eng., Chandler, 255041.46), (Biology, Candlestick, 647610.55), (History, Taylor, 699140.86), CREATE TABLE `course` ( course_id varchar(8) PRIMARY KEY, title varchar(50), dept_name varchar(20) REFERENCES department(dept_name), credits numeric(2,0) ); Sample rows from `course`: (787, C Programming, Mech. Eng., 4), (238, The Music of Donovan, Mech. Eng., 3), (608, Electron Microscopy, Mech. Eng., 3), CREATE TABLE `instructor` ( ID varchar(5) PRIMARY KEY, name varchar(20), dept_name varchar(20) REFERENCES department(dept_name), salary numeric(8,2) ); Sample rows from `instructor`: (63395, McKinnon, Cybernetics, 94333.99), (78699, Pingr, Statistics, 59303.62), (96895, Mird, Marketing, 119921.41), CREATE TABLE `section` ( course_id varchar(8) PRIMARY KEY REFERENCES course(course_id), sec_id varchar(8) PRIMARY KEY, semester varchar(6) PRIMARY KEY, year numeric(4,0) PRIMARY KEY, building varchar(15) REFERENCES classroom(building), room_number varchar(7) REFERENCES classroom(room_number), time_slot_id varchar(4) ); Sample rows from `section`: (313, 1, Fall, 2010, Chandler, 804, N), (747, 1, Spring, 2004, Gates, 314, K), (443, 1, Spring, 2010, Whitman, 434, O), CREATE TABLE `teaches` ( ID varchar(5) PRIMARY KEY REFERENCES instructor(ID), course_id varchar(8) PRIMARY KEY REFERENCES section(course_id), sec_id varchar(8) PRIMARY KEY REFERENCES section(sec_id), semester varchar(6) PRIMARY KEY REFERENCES section(semester), year numeric(4,0) PRIMARY KEY REFERENCES section(year) ); Sample rows from `teaches`: (34175, 747, 1, Spring, 2004), (3199, 169, 1, Spring, 2007), (6569, 445, 1, Spring, 2001), CREATE TABLE `student` ( ID varchar(5) PRIMARY KEY, name varchar(20), dept_name varchar(20) REFERENCES department(dept_name), tot_cred numeric(3,0) ); Sample rows from `student`: (24746, Schrefl, History, 4), (79352, Rumat, Finance, 100), (76672, Miliko, Statistics, 116), CREATE TABLE `takes` ( ID varchar(5) PRIMARY KEY REFERENCES student(ID), course_id varchar(8) PRIMARY KEY REFERENCES section(course_id), sec_id varchar(8) PRIMARY KEY REFERENCES section(sec_id), semester varchar(6) PRIMARY KEY REFERENCES section(semester), year numeric(4,0) PRIMARY KEY REFERENCES section(year), grade varchar(2) ); Sample rows from `takes`: (65901, 401, 1, Fall, 2003, C-), (24932, 802, 1, Spring, 2003, B-), (61332, 200, 1, Spring, 2007, A-), CREATE TABLE `advisor` ( s_ID varchar(5) PRIMARY KEY REFERENCES student(ID), i_ID varchar(5) REFERENCES instructor(ID) ); Sample rows from `advisor`: (24746, 19368), (79352, 95030), (76672, 22591), CREATE TABLE `time_slot` ( time_slot_id varchar(4) PRIMARY KEY, day varchar(1) PRIMARY KEY, start_hr numeric(2) PRIMARY KEY, start_min numeric(2) PRIMARY KEY, end_hr numeric(2), end_min numeric(2) ); Sample rows from `time_slot`: (A, M, 8, 0, 8, 50), (A, W, 8, 0, 8, 50), (A, F, 8, 0, 8, 50), CREATE TABLE `prereq` ( course_id varchar(8) PRIMARY KEY REFERENCES course(course_id), prereq_id varchar(8) PRIMARY KEY REFERENCES course(course_id) ); Sample rows from `prereq`: (376, 130), (760, 169), (403, 345), "
# 将文本转换为 tokens，并获取 token 的总数
tokens = tokenizer.tokenize(text)
token_length = len(tokens)

print(f"Token Length: {token_length}")
